mapper.select:
        select s.securitiesid,s.securitiestype, m.closingprice,si.securitiesnum, si.price,si.total, m.datetime,
		(si.securitiesnum* m.closingprice-si.total) appraisementVal from securities s,securitiesInventory si,
		(select * from market where datetime = (select max(datetime) from market where datetime <=#{toDay})) m
		where s.securitiesid = si.securitiesid and  m.securitiesid = s.securitiesid and si.datetime = #{toDay}
		and si.fundId= #{fundId}
